import { useEffect, useState } from "react";

const App = () => {
  const [quote, setQuote] = useState("");
  const getQuote = () => {
    fetch("https://api.quotable.io/random")
      .then((response) => response.json())
      .then((data) => setQuote(data));
  };

  useEffect(() => getQuote(), []);

  return (
    <section
      className="section-center"
      style={{
        backgroundImage: "url( pic.jpg')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        backgroundColor: "", // specify the background color here
      }}
    >
      <div className="text-center">
        <h3 className="font">{quote.content}</h3>
        <i className="font">
          <p style={{ textAlign: "right" }}>-&nbsp;{quote.author}</p>
        </i>
        <button className="btn btn-secondary" onClick={getQuote}>
          Get Quote
        </button>
      </div>
    </section>
  );
};

export default App;
